def run2(s: str) -> int:
    stack = [-1]
    max_len = 0

    for i, char in enumerate(s):
        if char == '(':
            stack.append(i)
        else:
            stack.pop()
            if not stack:
                stack.append(i)
            else:
                max_len = max(max_len, i - stack[-1])

    return max_len



def run1(s: str) -> int:
    def get_items(s: str) -> list:
        result = []
        for i in range(len(s)):
            tmp = []
            for j in range(i, len(s)):
                tmp.append(s[j])
                result.append(''.join(tmp))
        return result

    def is_valid(item: str) -> bool:
        n = len(item)
        delta = 0
        for i in range(len(item)):
            if item[i] == "(":
                delta += 1
            else:
                delta -= 1
            if delta < 0:
                return False
        return delta == 0

    def filtered_valid_items_only(items: list) -> list:
        ans = []
        for i in range(len(items)):
            if is_valid(items[i]):
                ans.append(items[i])
        return ans

    def get_the_longest_item(items: list) -> str:
        ans = "" if not items else items[0]
        for i in range(1, len(items)):
            if len(ans) < len(items[i]):
                ans = items[i]
        return ans

    all_items = get_items(s)
    filtered_items = filtered_valid_items_only(all_items)
    longest_item = get_the_longest_item(filtered_items)
    return len(longest_item)


