def run4(s: str) -> int:
    def get_items(s: str) -> list:
        result = []
        for i in range(len(s)):
            tmp = []
            for j in range(i, len(s)):
                if j == i and s[j] == ")":
                    break
                tmp.append(s[j])
                result.append("".join(tmp))
        return result

    def is_valid(item: str) -> bool:
        n = len(item)
        delta = 0
        for i in range(len(item)):
            if item[i] == "(":
                delta += 1
            else:
                delta -= 1
            if delta < 0:
                return False
        return delta == 0

    def get_longest(items: list) -> int:
        ans = 0
        for i in range(len(items)):
            if is_valid(items[i]):
                ans = max(ans, len(items[i]))
        return ans

    all_items = get_items(s)
    longest_item = get_longest(all_items)
    return longest_item


def run5(s: str) -> int:
    ans = 0
    i = 0
    n = len(s)
    while i < len(s):
        current = s[i:]
        delta = 0
        for j in range(n - i):
            if current[j] == "(":
                delta += 1
            else:
                delta -= 1
            if delta == 0:
                ans = max(ans, j + 1)
            if delta < 0:
                break
            if delta > current.count(")"):
                break
        i += 1
    return ans

def run6(s: str) -> int:
    ans = 0
    i = 0
    delta = 0
    n = len(s)
    length = 0
    while i < n:
        if s[i] == "(":
            delta += 1
        else:
            delta -= 1
            length += 2
        right = s[i:].count(')')
        left = s[i:].count('(')
        if delta == 0:
            ans = max(ans, length)
            i += 1
            continue
        if delta < 0 or (right - 1 == delta and right - 1 == left):
            length = 2
            delta = 1

        i += 1
    return ans

